let outputObject;
function processText() {
  const courseSlug = document.getElementById("courseSlugInput").value;
  const selectedCourse = courseList.find(
    (course) => course.slug === courseSlug
  );

  if (!selectedCourse) {
    alert("Course not found!");
    return;
  }

  const userInput = document.getElementById("textInput").value;
  const sessionPattern = /%([^%]+)/g;
  const detailsPattern =
    /(?:([^\.]+)\.)?(?:([^\.]+)\.)?(?:([^\.]+)\.)?(?:([^\.]+)\.)?(?:([^\.]+)\.)?(?:\.(.+))?/;
  const topicPattern = /#(.+)/g;
  let sessions = [...userInput.matchAll(sessionPattern)].map(
    (match) => match[1]
  );

  if (sessions) {
    let parsedSessions = sessions.map((session) => {
      const splitedLines = session.split("#");
      const hasDot = splitedLines[0].charAt(splitedLines[0].length - 1) === ".";

      if (!hasDot) {
        splitedLines[0] = splitedLines[0] + ".";
        session = splitedLines.join("#");
      }

      let detailsMatch = session.match(detailsPattern);
      let title = detailsMatch[1]?.trim();
      let date = detailsMatch[3]?.trim();
      let time = detailsMatch[4]?.trim();
      let sessionType = detailsMatch[5]
        ? detailsMatch[5]?.trim()
        : "جلسه آموزشی";
      let topics = [...session.matchAll(topicPattern)].map((match) =>
        match[1].trim()
      );
      const mentor = mentorList.find(
        (mentor) => mentor.id == selectedCourse.mentor_id
      );

      const sessionLink = selectedCourse?.skyroom; 

      const [startTime, endTime] = convertTimeString(time) || [
        "00:00",
        "00:00",
      ];
      return {
        title,
        date: convertPersianDateToGregorian(date) || date,
        startTime,
        endTime,
        mentor,
        sessionLink,
        sessionType,
        sessionTopics: topics,
      };
    });

    outputObject = JSON.stringify(parsedSessions);
    document.getElementById("courseTitle").innerText = selectedCourse.title_en;

    const tbody = document.querySelector("#output tbody");
    tbody.innerHTML = "";

    parsedSessions.forEach((session, index) => {
      const row = document.createElement("tr");
      row.innerHTML = `
            <td>${index + 1}</td>
            <td>${session?.title}</td>
            <td>${session?.date}</td>
            <td>${session?.startTime ?? "00:00"}</td>
            <td>${session?.endTime ?? "00:00"}</td>
            <td>${session?.mentor?.title_fa}</td>
            <td>${session?.sessionLink || "N/A"}</td>
            <td>${session?.sessionType}</td>
            <td>${session?.sessionTopics.join("<br>")}</td>
          `;

      tbody.appendChild(row);
    });
  } else {
    alert("Invalid input format!");
  }
}

function copyOutput() {
  navigator.clipboard
    .writeText(outputObject)
    .then(() => alert("Output copied to clipboard!"))
    .catch((err) => console.error("Unable to copy to clipboard", err));
}

function convertTimeString(input) {
  if(!input) return;
  const convertedString = convertPersianNumeralToArabic(input);
  const timeRegex = /(\d{1,2}):(\d{2})/g;
  return convertedString.match(timeRegex);
}

function convertPersianDateToGregorian(input) {
  if(!input) return;
  const [day, month, year] = input.split(" ");
  const monthMap = {
    فروردین: "01",
    اردیبهشت: "02",
    خرداد: "03",
    تیر: "04",
    مرداد: "05",
    شهریور: "06",
    مهر: "07",
    آبان: "08",
    آذر: "09",
    دی: "10",
    بهمن: "11",
    اسفند: "12",
  };
  const gregorianDate = `${convertPersianNumeralToArabic(year)}-${
    monthMap[month]
  }-${convertPersianNumeralToArabic(day)}`;

  return gregorianDate;
}

function convertPersianNumeralToArabic(persianNumeral) {
  if(!persianNumeral) return;
  const persianDigits = "۰۱۲۳۴۵۶۷۸۹";
  return persianNumeral
    .split("")
    .map((char) =>
      persianDigits.indexOf(char) !== -1 ? persianDigits.indexOf(char) : char
    )
    .join("");
}

